                <SelectItem value="expert">Экспертный</SelectItem>
                <SelectItem value="emotional">Эмоциональный</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="count">Количество вариантов</Label>
